import React from "react";
import { uploadFile } from "../api";
import { notifySuccess, notifyError, notifyInfo } from "./Notifications";

function FileUpload({ setResult }) {
  const handleSubmit = async (e) => {
    e.preventDefault();
    const file = e.target.file.files[0];
    if (!file) {
      notifyError("Please select a file!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    notifyInfo("Uploading file...");

    try {
      const { data } = await uploadFile(formData);
      setResult(data);

      if (data.prediction && data.prediction.toLowerCase().includes("ransom")) {
        notifyError("ðŸš¨ Ransomware detected!");
      } else if (data.prediction && data.prediction.toLowerCase().includes("safe")) {
        notifySuccess("âœ… File is safe.");
      } else {
        notifySuccess("File uploaded successfully.");
      }
    } catch (err) {
      console.error(err);
      notifyError("Error uploading file.");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", gap: 8, alignItems: "center" }}>
      <input type="file" name="file" />
      <button type="submit">Upload</button>
    </form>
  );
}

export default FileUpload;
